import React, { useEffect, useState } from 'react';
import './App.css'
import data from './Student_Data.json' ;
import Card from './Component/Card/Card';
import { v4 as uuidv4 } from 'uuid';
import Form from './Component/Form/Form';
import { useHistory } from "react-router-dom";
import { MDBCol, MDBFormInline, MDBBtn } from "mdbreact";
// import { Button } from 'reactstrap';
import Modal from './Component/Modal/Modal'
import CardDetails from './Component/CardDetails/CardDetails'
let  uuidData = data.map(i=>{
return {
    ...i, 
    Id: uuidv4()
}

})

function App() {
const [fav, setFav] =useState([])
const [data, setData]=useState([])
const [deletedRecords, setDeletedRecords]=useState([])
const [careerUrl,setCareerUrl] =useState("")
const [employer,setEmployer] =useState("")
const [graduationDate,setGraduationDate] =useState("")
const [jobStartYear,setJobStartYear] =useState("")
const [jobTitle,setJobTitle] =useState("")
const [specialization,setSpecialization] =useState("")
const [universityName,setUniversityName] =useState("")
const [isSubmitDisabled,setIsSubmitDisabled] =useState(false)
const [isModalOpen,setModelOpen] = useState(false);
const [records, setRecords] = useState({});
const [searchText,setSearchText] = useState("");
const [searchInvoked,setSearchInvoked]=useState(false);
const [filteredData,setFilteredData]=useState([])
// console.log(deletedRecords,'deletedRecords');

const history= useHistory()

function getFavs() {
let empData= uuidData.filter(i=>fav.includes(i.Id))
let returnEmployerName = empData.map(i=>i.Employer)
return returnEmployerName.join(",")
}

function handleFormSubmit () {
    console.log('handleFormSubmit invoked')
    setIsSubmitDisabled(true)
    setTimeout(()=>{
        setIsSubmitDisabled(false)
    },3000)

    let objReady = {
        Career_Url:careerUrl,
        Employer:employer,
        Graduation_Year:graduationDate,
        Job_Start_Date:jobStartYear,
        Job_Title:jobTitle,
        Specialization:specialization,
        University_Name:universityName,
        Id:uuidv4()
    }
let copyData = [objReady,...data]

setData(copyData)

}
// function createNewRecord() {

// }

function deleteRecord (e,Id) {

let deletedRecord= data.filter((i)=>{
    e.stopPropagation();
 return i.Id ===Id 
})
console.log(deletedRecord,'deletedRecord');

let  copyDelRecords=[...deletedRecords]
copyDelRecords.push(deletedRecord[0])
setDeletedRecords(copyDelRecords)

/////////Logic for updating screen
let remainingRecord= data.filter((i)=>{
    return i.Id !==Id 
   })   
setData(remainingRecord)
console.log(remainingRecord,'remainingRecord');
}

useEffect(()=>{
setData(uuidData)
},[])

function handleRetrieveAllRecords() {
let mergedRecords = [...deletedRecords,...data]
console.log(mergedRecords,'mergedRecords');
setData(mergedRecords)
setDeletedRecords([])
}

function handleCardDetailsOnClick(Id){
    console.log("hi from card: ")
    let p = data.filter((i)=>(i.Id === Id));
    console.log(p[0]);
    setRecords(p[0]);
     setModelOpen(true);
}

function handleSearch(){
    if(searchText.length ===0){
        setSearchInvoked(false)
    }
    else {
        setSearchInvoked(true)
    }
   let copy =[...data];
   copy = copy.filter(i=>{
      return i.Employer.toLowerCase().includes(searchText.toLowerCase())
  })
  console.log(copy);
  if(setSearchInvoked){
    setFilteredData(copy)
 }
}

const univName =data.map((i,idx)=>{
const {Employer, Career_Url, Job_Title,Id} = i 

    return (
     <div onClick={()=> handleCardDetailsOnClick(Id)} className="cardhover">
    <Card 
    careerUrl={Career_Url}
    Employer={Employer}
    Job_Title={Job_Title}
    key={Id} 
    setFav={setFav}
    fav={fav}
    Id={Id}
    deleteRecord={deleteRecord}
    />
    </div>
    )
})  

return (  

<div>

   <div>
   {/* <Button color="danger">Danger!</Button> */}
     <br></br>
        <button style={{margin:'10px',padding:'10px'}} onClick={()=>setFav([])}>Clear All Favorites</button>
        <br></br>
        {/* <button onClick={()=>history.push("/")}>Go Home </button> */}

        <button style={{margin:'10px',padding:'10px'}} onClick={()=>handleRetrieveAllRecords()}>Retrieve All Records</button>
        <br></br>
        {/* <button onClick={()=>history.push(`/test?isSubmitDisabled=${isSubmitDisabled}`)}>Test</button> */}
            </div> 
        <div style={{margin:'10px',padding:'10px'}}>{`Total record :::${data.length}`}</div>
        <div style={{margin:'10px',padding:'10px'}}>{`Total deleted record :::${deletedRecords.length}`}</div>
        {/* <Modal
        buttonLabel= 'New Data to Add'
        title = 'Form'
        buttonColor = 'success'
        cta_primary = 'submit'
        cta_sec = 'cancel'
        handleFormSubmit={handleFormSubmit}
        isModalOpen = {isModalOpen}
        setModelOpen = {setModelOpen}
        >
        <Form 
        careerUrl={careerUrl}
        employer={employer}
        graduationDate={graduationDate}
        jobStartYear={jobStartYear}
        jobTitle={jobTitle}
        specialization={specialization}
        universityName={universityName}
        setCareerUrl={setCareerUrl}
        setEmployer={setEmployer}
        setGraduationDate={setGraduationDate}
        setJobStartYear={setJobStartYear}
        setJobTitle={setJobTitle}
        setSpecialization={setSpecialization}
        setUniversityName={setUniversityName}
        // handleFormSubmit={handleFormSubmit}
        isSubmitDisabled={isSubmitDisabled}
        />
        </Modal> */}

        <Modal
        title = 'display data'
        buttonLabel="Open"
        handleFormSubmit = {handleFormSubmit}
        isModalOpen = {isModalOpen}
        setModelOpen = {setModelOpen}
        >
       <CardDetails
        Employer = {records.Employer}
        careerUrl = {records.Career_Url}
        Job_Title = {records.Job_Title}
        Job_Start_Date = {records.Job_Start_Date}
        Specialization = {records.Specialization}
        University_Name = {records.University_Name}
        Id = {records.Id}
       />
        </Modal>
<br></br>
<div>
       <MDBFormInline className="md-form mr-auto mb-4">    
        <input className="form-control mr-sm-2" type="text" placeholder="Search" aria-label="Search" onChange={(e)=>setSearchText(e.target.value)}/>
        <MDBBtn gradient="aqua" rounded size="sm" type="button" className="mr-auto"  onClick={()=>handleSearch()}>
          Search
        </MDBBtn>
        </MDBFormInline>
        {searchInvoked}
</div>

        <div> 
        <h2 style={{margin:'10px',padding:'10px'}}>Favorite Company:</h2>
        {getFavs()}
        </div>
        <br></br>


        <h2 style={{margin:'10px',padding:'10px'}}>Here is list of companies</h2>
        <br></br>
        <br></br>
        <div>
        {univName}
        </div>  
        </div>



        ) ;      

        }

        export default App
