import React, { useEffect, useState } from 'react';
import './App.css'
// import data from './Student_Data.json' ;
import Card from './Component/Card/Card';
import { v4 as uuidv4 } from 'uuid';

import { useHistory } from "react-router-dom";
import { MDBCol, MDBFormInline, MDBBtn } from "mdbreact";
import { Button } from 'reactstrap';
import Modal from './Component/Modal/Modal'
import CardDetails from './Component/CardDetails/CardDetails'
import Checkbox from '@material-ui/core/Checkbox';
import { makeStyles } from '@material-ui/core/styles';
import LinearProgress from '@material-ui/core/LinearProgress';
import axios from'./Axios'
//   import history from './Component/History/history';
import RecordDetails from './Component/RecordDetails/RecordDetails';



function App() {
const [fav, setFav] =useState([])
const [data, setData]=useState([])
const [companyName,setcompanyName] =useState("")
const [searchText,setSearchText]=useState("")
const [searchInvoked,setSearchInvoked]=useState(false)
const [flag, setFlag] = useState(false)

const history= useHistory()

useEffect(()=>{
    setFlag(true);
   axios.get('/v1/record/getAllRecords')
    .then(
        res=>{
            console.log(res)
            setData(res.data)
            setFlag(false);
        })
    .catch((e)=>{
        setFlag(false)
        console.log(e)
    })
},[])

// ********* CHANGE *************

    const univName = data.map((i,idx,arr)=>{
        const {companyname, Career_Url, Job_Title,Id,Graduation_Year} = i 
        console.log(companyname);
        return (
            // <div className='cardDiv' onClick={()=>handleCardContainerOnClick(i.companyname)}>
            <Card 
            careerUrl={Career_Url}
            companyName={companyName}
            Job_Title={Job_Title}
            Graduation_Year={Graduation_Year}
            key={Id} 
            setFav={setFav}
            fav={fav}
            Id={Id}
            // deleteRecord={deleteRecord}
            />
            // </div>
            )

        })    

        useEffect(()=>{
          axios.get(`/v1/record/recordbyname?searchText=${searchText}`)
          .then(
            res=>{
                console.log(res)
                setData(res.data)
                setFlag(false);
            })
        .catch((e)=>{
            setFlag(false)
            console.log(e)
        })
        },[searchInvoked])

            function handleClear(){
                setSearchText("")
                setSearchInvoked(false)
                }

            function handleSearch () {
                if(searchText.length ===0){
                    setSearchInvoked(false)
                }
                else {
                    setSearchInvoked(true)
                }
            }

            function handleKeyPress (e) {
                if(e.which == 13 || e.keyCode == 13){
                //   console.log("enter press here!");
                  handleSearch()
                }
              }




if(flag){
    return <LinearProgress variant="determinate"/>
}

return (  
<div>
    <div>
    <input placeholder='Search with Company name'  value={searchText} onKeyPress={(e)=>handleKeyPress(e)} onChange={(e)=>setSearchText(e.target.value)}/>
    <span style={{marginLeft:'20px'}}><Button disabled={searchInvoked} onClick={()=>handleSearch()} color="primary">Search</Button></span>
        {
        searchInvoked && <span style={{marginLeft:'20px'}}>
            <Button onClick={()=>handleClear()} color="primary">Clear</Button>
        </span>
        }
    </div>

    <div>
    {}
    </div>  
</div>
);      
}

        export default App
