import React, { Fragment, useEffect, useState } from 'react';
import { Link, Redirect, useHistory,useParams } from "react-router-dom";
import axiosInstance from '../../Axios'
import Spinner from '@material-ui/core/LinearProgress';
import CardDetails from '../CardDetails/CardDetails';

function RecordDetails(){

        const [flag, setFlag] = useState(false)
        const [data, setData]=useState([])
        console.log("data")
        console.log(data);
        console.log("Career_Url");
        console.log(data[0].Employer)
        const params = useParams()
        // console.log(params,'params')


        useEffect(()=>{
            setFlag(true);
        axiosInstance.get(`/recordsdetails/${params.Id}`)
            .then(
                res=>{
                    // console.log(res)
                    setData(res.data)
                    setFlag(false);
                })
            .catch((e)=>{
                setFlag(false)
                console.log(e)
            })
        },[])

            if(flag){
                return <Spinner />
            }

            return (
                <div>
        <CardDetails
            careerUrl={data[0].Career_Url}
            Employer={data[0].Employer}
            Job_Title={data[0].Job_Title}
            Graduation_Year={data[0].Graduation_Year}
            key={data.Id} 
            setFav={data[0].setFav}
            fav={data.fav}
            Id={data.Id}
        />
                </div>
            )
        }
export default RecordDetails;
